$(document).ready(function () { function t() { $("div[id$=divAnswers] img").each(function () { var e = $(this).attr("val"); $(this).css({ width: "0%" }).animate({ width: e }, "slow") }) } var e = new Image; e.src = "../../images/blue-bar.png"; if ($("#divVoted").length > 0) { t() } else { $("#btnSubmit").click(function () { var e = false; $("input[name='rdoPoll']").each(function () { if ($(this).is(":checked")) { e = true; return } }); if (!e) { $("#ErrMsg").html("اختر اجابة من فضلك") } else { $("#divPoll").css("cursor", "wait"); $("#btnSubmit").attr("disabled", "true"); var n = $("input[id$=hidPollID]").val(); var r = $("input[name='rdoPoll']:checked").val(); var i = $("input[id$=hidSiteName]").val(); var s = $("input[id$=hidLang]").val(); var o = $("input[id$=hidZoneName]").val(); var u = $("input[id$=hidTotalMsg]").val(); var a = $("input[id$=hidbarimg]").val(); var f = $("input[id$=hidAlreadyVoted]").val(); var l = "{'pID':'" + n + "', 'cID':'" + r + "', 'SiteName':'" + i + "', 'Lang':'" + s + "', 'ZoneName':'" + o + "', 'TotalVotesMsg':'" + u + "', 'BarImg':'" + a + "', 'AlreadyVoted':'" + f + "'}"; var c = "http://" + window.location.hostname + "/bridge.aspx/UpdatePollCount"; $.ajax({ type: "POST", url: c, data: l, contentType: "application/json; charset=utf-8", dataType: "json", success: function (e) { $("#divPoll").css("cursor", "default"); $("#btnSubmit").attr("disabled", "false"); if (e.d == null) { $("div[id$=divAnswers]").fadeOut("fast").html(e).fadeIn("fast", function () { t() }) } else { $("div[id$=divAnswers]").fadeOut("fast").html(e.d).fadeIn("fast", function () { t() }) } } }) } }) } })